import React from 'react';
import styled, {ThemeProvider, keyframes}  from 'styled-components';
import themeGreen from './ThemeGreen';

import Target from './Target';
import TopBar from './TopBar';
import InfoBar from './InfoBar';
import ToolTip from './ToolTip';

const animatedGrow = ()=> {
  return keyframes`
    0% {
      width: 0;
    }
    100% {
      width: ${props => props.percent || '5'}%;
    }
  `
}

const Wrapper = styled.section`
  height: 8em;
  padding: 3em 2em;
  max-width: 40em;
  background: ${props => props.theme.main};
  margin: 2em auto;
  border-radius: 10px;
  position: relative;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  @media (max-width: 500px) {
      padding: 4em 2em;
 }
`

const Label = styled.span`
  color: #FFF;
`

const FullBar = styled.div`
  width: 65%;
  height: 2em;
  background: ${props => props.theme.third};
  position: absolute;
  top:26%;
  left: 120px;
  z-index: 1;
  box-shadow: inset 0px 1px 2px ${props => props.theme.shadow};
  @media (max-width: 500px) {
   flex-direction: column;
   top: 30%;
   width: 45%;
  }
`

const Progress = styled.div`
  height: 2em;
  width: ${props => props.percent}%;
  background: ${props => props.theme.second};
  position: absolute;
  top: 0;
  left: 0;
  z-index: 2;
  animation: 1s ${animatedGrow} ease-out;
`

const ProgressBar = () => {

  const current = 56;
  const max = 125;
  const reached = max - current;
  return (<ThemeProvider theme={themeGreen}>
    <Wrapper>
      <TopBar>Your Progress</TopBar>
      <Label>Reached:</Label>
      <FullBar>
        <Progress percent={current} />
        <ToolTip current={current} />
      </FullBar>
      <Target max={max} />
      <InfoBar reached={reached} />

    </Wrapper>
  </ThemeProvider>)
}

ThemeProvider.defaultProps = {
  theme: {
    main: '#71C9CE',
    second: '#82AFB3',
    third: '#CBF1F5',
    shadow: '#888888'
  }
}
export default ProgressBar;
