import React, { useState } from 'react';
import {useSpring, animated} from 'react-spring';
import axios from 'axios';
import {API_BASE_URL, ACCESS_TOKEN_NAME} from '../constants/apiConstants';
import './LoginRegister.css'

function LoginRegister() {
    const [registrationFormStatus, setRegistrationFormStatus] = useState(false);
    const loginProps = useSpring({
        left: registrationFormStatus ? -500 : 0,
        opacity: registrationFormStatus ? 0 : 1
    })
    const registerProps = useSpring({
        left: registrationFormStatus ? 0 : 500,
        opacity: registrationFormStatus ? 1 : 0
    })

    const loginBtnProps = useSpring({
        borderBottom: registrationFormStatus ? 'solid 0px transparent' : 'solid 2px #70C217' 
    })
    const registerBtnProps = useSpring({
        borderBottom: registrationFormStatus ? 'solid 2px #70C217' : 'solid 0px transparent' 
    })

    function registerClicked() { setRegistrationFormStatus(true)}
    function loginClicked() { setRegistrationFormStatus(false)}

    return (
        <div className="login-register-wrapper">
            <div className="nav-buttons">
                <animated.button onClick= {loginClicked} id="loginBtn" style={loginBtnProps}>Login</animated.button>
                <animated.button onClick= {registerClicked} id="registerBtn" style={registerBtnProps}>Register</animated.button>
            </div>
            <div className="form-group">
                <animated.form action='' id='loginform' style={loginProps}><LoginForm /></animated.form>
                <animated.form action='' id='registerform' style={registerProps}><RegisterForm /></animated.form>
                <animated.div className="forgot-panel" style={loginProps}><a href="#forgot">Forgot your password</a></animated.div>
            </div>
        </div>
    )
}

function LoginForm(props) {
    const [state , setState] = useState({
        Username : "",
        password : "",
        successMessage: null
    })
    const handleChange = (e) => {
        const {id , value} = e.target   
        setState(prevState => ({
            ...prevState,
            [id] : value
        }))
    }

    const handleSubmitClick = (e) => {
        e.preventDefault();
        const payload={
            "username":state.username,
            "password":state.password,
        }
        axios.post(API_BASE_URL+'/user/login', payload)
            .then(function (response) {
                if(response.status === 200){
                    setState(prevState => ({
                        ...prevState,
                        'successMessage' : 'Login successful. Redirecting to home page..'
                    }))
                    localStorage.setItem(ACCESS_TOKEN_NAME,response.data.token);
                    redirectToHome();
                    props.showError(null)
                }
                else if(response.code === 204){
                    props.showError("Username and password do not match");
                }
                else{
                    props.showError("Username does not exist");
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    }
    const redirectToHome = () => {
        props.updateTitle('Home')
        props.history.push('/home');
    }
    return (
    <React.Fragment>
        <label for='username'>Username</label>
        <input type='text' 
        id='username' 
        value={state.username}
        placeholder='CircleAfrica' 
        onChange={handleChange}
        />

        <label for='password'>Password</label>
        <input type='text' 
        id='password' 
        placeholder='**********' 
        value={state.password}
        onChange={handleChange}
        />

        <input type='submit' 
        value='submit' 
        className='submit' 
        onClick={handleSubmitClick}
        />

        <div className="alert alert-success" style={{display: state.successMessage ? 'block' : 'none' }} role="alert">
            {state.successMessage}
        </div>
    </React.Fragment>
    )
}

function RegisterForm(props) {
    const [state , setState] = useState({
        firstname : "",
        lastname : "",
        middlename : "",
        phone : "",
        email : "",
        address : "",
        username : "",
        password : "",
        confirmPassword: "",
        farmer : "",
        investor : "",
        consultant : "",
        successMessage: null
    })
    const handleChange = (e) => {
        const {id , value} = e.target   
        setState(prevState => ({
            ...prevState,
            [id] : value
        }))
    }
    const sendDetailsToServer = () => {
        if(state.email.length && state.password.length) {
            props.showError(null);
            const payload={
                "firstname":state.firstname,
                "lastname":state.lastname,
                "middlename":state.middlename,
                "phone":state.phone,
                "email":state.email,
                "address":state.address,
                "password":state.password,
            }
            axios.post(API_BASE_URL+'/user/register', payload)
                .then(function (response) {
                    if(response.status === 200){
                        setState(prevState => ({
                            ...prevState,
                            'successMessage' : 'Registration successful. Redirecting to home page..'
                        }))
                        localStorage.setItem(ACCESS_TOKEN_NAME,response.data.token);
                        redirectToHome();
                        props.showError(null)
                    } else{
                        props.showError("Some error ocurred");
                    }
                })
                .catch(function (error) {
                    console.log(error);
                });    
        } else {
            props.showError('Please enter valid username and password')    
        }
        
    }
    const redirectToHome = () => {
        props.updateTitle('Home')
        props.history.push('/home');
    }
    const handleSubmitClick = (e) => {
        e.preventDefault();
        if(state.password === state.confirmPassword) {
            sendDetailsToServer()    
        } else {
            props.showError('Passwords do not match');
        }
    }
    return (
        <React.Fragment>
            <label for="firstname">First Name</label>
            <input type="text" 
            id="firstname" 
            placeholder='Lambert' 
            value={state.firstname}
            onChange={handleChange} 
            />

            <label for="lastname">Last Name</label>
            <input type="text" 
            id="lastname" 
            placeholder='Agbo'  
            value={state.lastname}
            onChange={handleChange}
            />

            <label for="middlename">Middle Name</label>
            <input type="text" 
            id="middlename" 
            placeholder='Ikechukwu' 
            value={state.middlename}
            onChange={handleChange}
            />

            <label for="phone">Phone</label>
            <input type="text" 
            id="phone" 
            placeholder='+234-703-5671234' 
                        value={state.phonenumber}
            onChange={handleChange}
            />

            <label for="email">E-mail</label>
            <input type="text" 
            id="email" 
            placeholder='circleafrica@gmail.com' 
            onChange={handleChange}
            />

            <label for="address">Address</label>
            <input type="text" 
            id="address" 
            placeholder='No. 30 Ekpoma Street, New Haven, Enugu' 
            onChange={handleChange}
            />

            <label for='username'>Username</label>
            <input type='text' 
            id='username' 
            placeholder='CircleAfrica' 
            onChange={handleChange}
            />

            <label for='password'>Password</label>
            <input type='text' 
            id='password' 
            placeholder='**********' 
            onChange={handleChange}
            />

            <label for='password'>Confirm Password</label>
            <input type='text' 
            id='password' 
            placeholder='**********' 
            onChange={handleChange}
            />

            <div className="radio-input-group">
                <input className="farmer-radio" 
                type="radio" 
                value="Male" 
                name="gender" /> Farmer

                <input className="investor-radio" type="radio" 
                value="Female" 
                name="gender" /> Investor

                <input className="consultant-radio" type="radio" 
                value="Other" 
                name="gender" /> Consultant
            </div>

            <input type='submit' 
            value='submit' 
            className='submit' 
            onClick={handleSubmitClick}
            />

            <div className="alert alert-success" style={{display: state.successMessage ? 'block' : 'none' }} role="alert">
                {state.successMessage}
            </div>
        </React.Fragment>
    )
}

export default LoginRegister;
