        return (
            <React.Fragment onSubmit={this.handleSubmit}>
                <label for="firstname">First Name</label>
                <input type="text" 
                id="firstname" 
                placeholder='Lambert' 
                // onChange={e => state.firstname = e.target.value} 
                />
    
                <label for="lastname">Last Name</label>
                <input type="text" 
                id="lastname" 
                placeholder='Agbo'  
                // value={state.lastname}
                // onChange={handleChange}
                />
    
                <label for="middlename">Middle Name</label>
                <input type="text" 
                id="middlename" 
                placeholder='Ikechukwu' 
                // value={state.middlename}
                // onChange={handleChange}
                />
    
                <label for="phone">Phone</label>
                <input type="text" 
                id="phone" 
                placeholder='+234-703-567-1234' 
                // value={state.phone}
                // onChange={handleChange}
                />
    
